
mw.centralNotice.insertBanner( {"bannerHtml":"\n\u003Cstyle\u003E\n\n@font-face {\n  font-family: 'Montserrat';\n  font-style: normal;\n  src: local('Montserrat'), local('Montserrat'), url(https://upload.wikimedia.org/wikipedia/donate/c/cd/Montserrat.woff2) format('woff2');}\n\n/* Main banner container. Banner: background gradient, border colour and width and adding a margin between banner and article header*/\n/* Important -- background gradient -- If you change one you must change all*/\n.cnotice {\n    position: relative;\n    overflow: hidden;\n    background: #FCFCFC;\n    border: 2px solid #D47E2B;\n    border-radius: 2px;\n    margin-bottom: 1em;\n    cursor: pointer;\n    color: #222;\n    font-weight: 500;\n}\n\n/* Sets the minimum banner height. If img + logo-container margins \u003E height. Banner will be larger*/\n#cnotice-main {\n    display: table;\n    width: 100%;\n    height: 80px; /* need to set height for height 100% to work on elements within it */\n}\n\n/*MOBILE - Adjust mobile height of banner*/\nbody.skin-minerva #cnotice-main {\n    height: 100px;\n}\n\n/*IMPORTNT - don't touch*/\n.cnotice-message-container,\n.cnotice-logo-container,\n.cnotice-misc-container {\n    display: table-cell;\n    height: 100%;\n    vertical-align: middle;\n}\n\n/* --- Main message --- */\n.cnotice-message {\n    position: relative;\n    margin: 0;\n    line-height: 1.2;\n    padding: 5px 5px 5px 5px;\n}\n\nbody.rtl .cnotice-message {\n    padding: 5px 5px 5px 5px;\n}\n\n.cnotice-message p {\n    margin: 0;\n    color: #4D4D4D;\n    font-family: Arial, sans-serif;\n    font-weight: bold;\n    font-size: 100%;\n    opacity: 1;\n    text-align: center;\n    padding-top: 10px;\n    padding-bottom: 10px;\n}\n\n@media (min-width: 1200px) {\n    .cnotice-message p { font-size: 120%;}\n}\n\n\n/* --- Logo Image text --- */\n.cnotice-logo-container {\n    width: 10%;\n}\n\nbody.rtl .cnotice-logo-container {\n    padding: 0.0em .25em 0.0em .9em;\n}\n\n.cnotice-logo-container img {\n    opacity: 1;\n    display: block;\n    margin-top: 2px;\n    margin-bottom: 1px;\n    padding-left: 4px;\n}\n\n/*Misc containter stuff starts here*/\n.cnotice-misc-container {\n    width: 10%;\n    padding-right: 40px;\n}\n\n/*Adjusts for screen size */\n@media (min-width: 1200px) {\n    .cnotice-misc-container {\n         padding-right: 40px;\n    }\n}\n\n/*Detects and adjusts for mobile skin */\nbody.skin-minerva .cnotice-misc-container {\n    padding-right: 10px;\n}\n\n\n/* --- Close Options --- */\n\n#cnotice-toggle-box-options {\n    display: table-cell;\n    font-size: .8em;\n    width: 38px;\n    height: 38px;\n    \n    background: #eee;\n    -moz-border-radius: 19px;\n    -webkit-border-radius: 19px;\n    border-radius: 19px;\n    vertical-align: middle;\n}\n\n#cnotice-toggle-box {\n    cursor: pointer;\n    position: absolute;\n    top: 2px;\n    right: 3px;\n    z-index: 1;\n}\n\nbody.rtl #cnotice-toggle-box {\n    left: 3px;\n    right: unset;\n}\n\n#cnotice-toggle-box:hover { opacity: 1; }\n\n#cnotice-toggle-box-x {\n    background: url(https://upload.wikimedia.org/wikipedia/commons/c/ce/Black_close_x.svg) no-repeat center center;\n    width: 100%;\n    height: 50%;\n}\n\nbody.rtl #cnotice-toggle-box-x {\n    float: left;\n}\n\n/* --- Full Banner Link --- */\n.cnotice a.cnotice-full-banner-click {\n    display: block;\n    height: 100%;\n    width: 100%;\n    cursor: pointer;\n    text-decoration: none;\n}\n\n.cnotice a.cnotice-full-banner-click:hover {\n    text-decoration: underline;\n}\n\n.cnotice-button {\n    border: none;\n    display: inline-block;\n    border: solid 2px #36c;\n    border-radius: 4px;\n    color: white;\n    padding: 5px 5px;\n    text-align: center;\n    font-weight: bold;\n    text-decoration: none;\n    font-size: 12px;\n    margin: 4px 2px;\n    -webkit-transition-duration: 0.4s; /* Safari */\n    transition-duration: 0.4s;\n    cursor: pointer;\n    z-index: 1;\n    min-width: 120px;\n}\n\n@media (min-width: 1100px) {\n    .cnotice-button {\n        display: inline-block;\n        font-size: 14px;\n        min-width: 140px;\n        width: 25%;\n        margin: auto;\n    }\n}\n\n/* --- Buttons --- */\n.cnotice a.cnotice-buttonlink {\n    cursor: pointer;\n    text-decoration: none;\n    color: black;\n}\n\n.cnotice a.cnotice-buttonlink:hover {\n    color: white;\n}\n\n.cnotice-button1 {\n    background-color: #36c;\n    color: #fff;\n}\n\n.rtl .cnotice-button1 {\n    left: 23px;\n    right: auto;\n    margin-left: 20px;\n}\n\n.cnotice-button1:hover {\n    background-color: #447ff5;\n}\n\n.cnotice-button1:active {\n    background-color: #2a4b8d;\n}\n\n#cnotice-translation-link {\n  position: absolute;\n  right: 65px;\n  bottom: 0px;\n  font-size: 0.8em;\n  white-space: nowrap;\n}\n\n#cnotice-translation-link:hover {\n  text-decoration: underline;\n}\n\n.rtl #cnotice-translation-link {\n  text-align: left;\n  left: 95px;\n}\n\u003C/style\u003E\n\n\u003Cdiv class=\"cnotice\" id=\"asian_month_2019\"\u003E\n    \u003Ca class=\"cnotice-full-banner-click external text\" href=\"https://fr.wikipedia.org/wiki/Wikip\u00e9dia:Mois_asiatique_Wikip\u00e9dia/2019\"\u003E\n        \u003Cdiv id=\"cnotice-main\"\u003E\n            \u003Cdiv class=\"cnotice-logo-container\"\u003E\n                \u003Cimg src=\"//upload.wikimedia.org/wikipedia/commons/thumb/1/1f/WAM_logo_without_text.svg/70px-WAM_logo_without_text.svg.png\" alt=\"Banner logo\"\u003E\n            \u003C/div\u003E\n            \u003Cdiv class=\"cnotice-message-container\"\u003E\n                \u003Cdiv class=\"cnotice-message\"\u003E\n                    \u003Cp\u003E\n                         En novembre, c\u0026#039;est le Mois asiatique Wikip\u00e9dia.\u003Cbr /\u003E\n                         Rejoignez le concours et gagnez une carte postale envoy\u00e9e depuis l\u0026#039;Asie.\n                    \u003C/p\u003E\n                \u003C/div\u003E\n            \u003C/div\u003E\n        \u003C/div\u003E\n    \u003C/a\u003E   \n\u003Cdiv id=\"cnotice-translation-link\"\u003E\n      [\u003Ca href=\"//meta.wikimedia.org/w/index.php?title=Special:Translate\u0026group=Centralnotice-tgroup-asian_month_2019\"\u003EAidez-nous \u00e0 traduire !\u003C/a\u003E]\n\u003C/div\u003E \n\u003Cdiv id=\"cnotice-toggle-box\"\u003E\n    \u003Ca href=\"#\" title=\"R\u00e9duire\" onclick=\"mw.centralNotice.hideBanner();return false;\"\u003E\u003Cimg border=\"0\" src=\"//upload.wikimedia.org/wikipedia/foundation/2/20/CloseWindow19x19.png\" alt=\"R\u00e9duire\" /\u003E\u003C/a\u003E\n\u003C/div\u003E\n\u003Cscript\u003E\n    if ( mw.config.get( 'wgDBname' ) === \"enwiki\" ) {\n        // Override link for enwiki and still use meta link as fallback\n        document.querySelector('.cnotice-full-banner-click').href = \"https://en.wikipedia.org/wiki/Wikipedia:Wikipedia_Asian_Month/2019\";\n    }\n\u003C/script\u003E"} );